<?phperror_reporting(E_ALL ^ E_NOTICE ^ E_WARNING);include 'includes/database.php';require 'vendor/autoload.php'; // PhpSpreadsheet autoloaderuse PhpOffice\PhpSpreadsheet\Spreadsheet;use PhpOffice\PhpSpreadsheet\Writer\Xlsx;use PhpOffice\PhpSpreadsheet\Style\Fill;$get_date = (isset($_REQUEST['get_date']) && $_REQUEST['get_date'] != '') ? trim($_REQUEST['get_date']) : date('Y-m-d');/* $u_query = "SELECT a.*, b.store_name, b.city, b.pos, b.acquirer, b.tid, b.payment_mode, b.transaction_id, b.approval_code, b.amount, b.datetime, b.txn_status, b.rrn, b.card_network, b.card_colour, b.emi_txn, b.emi_month, b.card_type_trns, c.voucher_no, c.apr_no, c.invoice_no, c.trn_date, c.amount AS apx_amt    FROM happi_hdfc_transactions a     JOIN happi_all_transactions b ON(b.approval_code = a.approve_code AND b.acquirer = 'HDFC')     JOIN happi_apx_transactions c ON(c.apr_no = a.approve_code)     WHERE a.trans_date = '$get_date'    ORDER BY a.id ASC"; */		$u_query = "SELECT * FROM happi_bajaj_apx_transactions a join happi_bajaj_do_transactions c on (c.do_id = a.apr_no)join happi_bajaj_ta_transactions d on (d.txn_id = c.app_cas_id)join happi_dbd_master_data b on (b.invoice_no = a.invoice_no) where a.trn_date = '$get_date';"; //echo $u_query;exit;	//echo $u_query;exit;$u_res = mysqli_query($conn, $u_query);$u_count = mysqli_num_rows($u_res);$spreadsheet = new Spreadsheet();$sheet = $spreadsheet->getActiveSheet();// Set headers$headers = array(    'Branch Name',    'Customer Name',    'Txn Date',    'Txn ID',    'Deal ID',    'Invoice No',    'APX Aproval No',    'APX Invoice Date',    'Amount Fin',    'APX Amount',    'Disb Amount',    'DBD%',    'Bajaj DBD Charges',    'APX DBD',    'Difference DBD Charges');$sheet->fromArray($headers, NULL, 'A1');$rowIndex = 2;if ($u_count > 0) {    while ($row = mysqli_fetch_assoc($u_res)) {        $apx_amt = $row['apx_amt'];		 $disb_amt = $row['disb_amt'];		 if($apx_amt == '')			 $apx_amt = 0;		 $dbm_main = $apx_amt - $disb_amt;		 $dbd_per = ($dbm_main / $apx_amt) * 100;		 if($apx_amt == 0){			 $dbd_per = '-';		 }		 		 $stdate = explode('-',$row['trans_date']); 		$trans_date = $stdate[2].'-'.$stdate[1].'-'.$stdate[0];				$stdate1 = explode('-',$row['trn_date']); 		$trn_date = $stdate1[2].'-'.$stdate1[1].'-'.$stdate1[0];		 		//if($apx_amt != '') {			$lineData = array(				$row['branch_name'],				$row['customer_name'],				$trans_date,				",".$row['txn_id'],				$row['do_id'],				$row['apx_invoice_no'],				$row['apr_no'],				$trn_date,				$row['amt_fn'],				$row['apx_amt'],				$row['disb_amt'],				round($dbd_per,2,2) . '%',				$row['dis_val'],				$dbm_main,				($dbm_main - $row['dis_val'])			);			$sheet->fromArray($lineData, NULL, 'A' . $rowIndex);			// Highlight red if Debuted MDR > 100		  /*  if ($debutedmdr > $actual_mdr) {				$sheet->getStyle("A{$rowIndex}:AB{$rowIndex}")->applyFromArray([					'fill' => [						'fillType' => Fill::FILL_SOLID,						'startColor' => ['rgb' => 'FF9999']					]				]);			} */			$rowIndex++;		    }}// Output as Excel$filename = 'Apx-Bajaj-Matched-BULK-' . $get_date . '.xlsx';header('Content-Type: application/vnd.openxmlformats-officedocument.spreadsheetml.sheet');header("Content-Disposition: attachment;filename=\"$filename\"");header('Cache-Control: max-age=0');$writer = new Xlsx($spreadsheet);$writer->save('php://output');exit;