<?phperror_reporting(E_ALL ^ E_NOTICE ^ E_WARNING);include 'includes/database.php';require 'vendor/autoload.php'; // PhpSpreadsheet autoloaderuse PhpOffice\PhpSpreadsheet\Spreadsheet;use PhpOffice\PhpSpreadsheet\Writer\Xlsx;use PhpOffice\PhpSpreadsheet\Style\Fill;$get_date = (isset($_REQUEST['get_date']) && $_REQUEST['get_date'] != '') ? trim($_REQUEST['get_date']) : date('Y-m-d');/* $u_query = "SELECT a.*, b.store_name, b.city, b.pos, b.acquirer, b.tid, b.payment_mode, b.transaction_id, b.approval_code, b.amount, b.datetime, b.txn_status, b.rrn, b.card_network, b.card_colour, b.emi_txn, b.emi_month, b.card_type_trns, c.voucher_no, c.apr_no, c.invoice_no, c.trn_date, c.amount AS apx_amt    FROM happi_hdfc_transactions a     JOIN happi_all_transactions b ON(b.approval_code = a.approve_code AND b.acquirer = 'HDFC')     JOIN happi_apx_transactions c ON(c.apr_no = a.approve_code)     WHERE a.trans_date = '$get_date'    ORDER BY a.id ASC"; */		$u_query = "SELECT * FROM happi_idfc_apx_transactions aLEFT JOIN happi_idfc_ta_transactions d ON (d.sfdc_id = a.apr_no)LEFT JOIN happi_dbd_master_data b ON (b.invoice_no = a.invoice_no)LEFT JOIN happi_idfc_kvb_transactions c ON (c.utr_no_dec = d.neft_utr_no)WHERE a.trn_date = '$get_date'  AND (        d.sfdc_id IS NULL     OR b.invoice_no IS NULL     OR c.utr_no_dec IS NULL  );;"; //echo $u_query;exit;	//echo $u_query;exit;$u_res = mysqli_query($conn, $u_query);$u_count = mysqli_num_rows($u_res);$spreadsheet = new Spreadsheet();$sheet = $spreadsheet->getActiveSheet();// Set headers$headers = array(    'Branch Name',    'Customer Name',    'Txn Date',    'Txn ID',    'Deal ID',	'DBD ID',    'Invoice No',    'Amount Fin',    'APX Amount',    'Disb Amount',    'DBD%',    'IDFC DBD Charges',    'APX DBD',    'Difference DBD Charges');$sheet->fromArray($headers, NULL, 'A1');$rowIndex = 2;if ($u_count > 0) {    while ($row = mysqli_fetch_assoc($u_res)) {        $apx_amt = $row['apx_amt'];		 $credit_amount = $row['credit_amount'];		 if($apx_amt == '')			 $apx_amt = 0;		 $dbm_main = $apx_amt - $credit_amount;		 $dbd_per = ($dbm_main / $apx_amt) * 100;		 if($apx_amt == 0){			 $dbd_per = '-';		 }		 		 $stdate = explode('-',$row['transaction_date']); 		$transaction_date = $stdate[2].'-'.$stdate[1].'-'.$stdate[0];					//if($apx_amt != '') {			$lineData = array(				$row['salespoint_name'],				$row['applicant_name'],				$transaction_date,				"'".$row['sfdc_id'],				"'".$row['application_id'],				"'".$row['utr_no_dec'],				$row['invoice_no'],				$row['credit_amount'],				$row['apx_amt'],				$row['dbd_amt'],				round($dbd_per, 2, 2) . '%',				$row['dbd_inlclude_gst'],				$dbm_main,				($dbm_main - $row['dbd_inlclude_gst'])			);			$sheet->fromArray($lineData, NULL, 'A' . $rowIndex);			// Highlight red if Debuted MDR > 100		  /*  if ($debutedmdr > $actual_mdr) {				$sheet->getStyle("A{$rowIndex}:AB{$rowIndex}")->applyFromArray([					'fill' => [						'fillType' => Fill::FILL_SOLID,						'startColor' => ['rgb' => 'FF9999']					]				]);			} */			$rowIndex++;		    }}// Output as Excel$filename = 'APX-IDFC-Not-Matched-BULK-' . $get_date . '.xlsx';header('Content-Type: application/vnd.openxmlformats-officedocument.spreadsheetml.sheet');header("Content-Disposition: attachment;filename=\"$filename\"");header('Cache-Control: max-age=0');$writer = new Xlsx($spreadsheet);$writer->save('php://output');exit;